jQuery.fn.highlight = function(pat) {
 function innerHighlight(node, pat) {
  var skip = 0;
  if (node.nodeType == 3) {
   var pos = node.data.toUpperCase().indexOf(pat);
   if (pos >= 0) {
    var spannode = document.createElement('span');
    spannode.className = 'highlight';
    var middlebit = node.splitText(pos);
    var endbit = middlebit.splitText(pat.length);
    var middleclone = middlebit.cloneNode(true);
    spannode.appendChild(middleclone);
    middlebit.parentNode.replaceChild(spannode, middlebit);
    skip = 1;
   }
  }
  else if (node.nodeType == 1 && node.childNodes && !/(script|style)/i.test(node.tagName)) {
   for (var i = 0; i < node.childNodes.length; ++i) {
    i += innerHighlight(node.childNodes[i], pat);
   }
  }
  return skip;
 }
 return this.each(function() {
  innerHighlight(this, pat.toUpperCase());
 });
};

jQuery.fn.removeHighlight = function() {
 return this.find("span.highlight").each(function() {
  this.parentNode.firstChild.nodeName;
  with (this.parentNode) {
   replaceChild(this.firstChild, this);
   normalize();
  }
 }).end();
};
(function() {
  if (!(window.console != null) || !(window.console.log != null)) {
    window.console = {
      log: function() {
        return false;
      }
    };
  }
  jQuery(function() {
    return $("p.flash").click(function() {
      return $(this).hide();
    });
  });
}).call(this);
(function() {
  var callback;
  callback = function(data) {
    var notice;
    console.log(data);
    $("#" + data.book_id + "-" + data.name).text(data.count).parent().addClass('notice');
    notice = $("<div class='notice'>" + data.message + "</div>");
    $("#messages").append(notice);
    return window.setTimeout((function() {
      return notice.remove();
    }), 2000);
  };
  jQuery(function() {
    $('a.like_it').live('click', function() {
      $.post($(this).attr('href') + '.json', {
        mark: 'like_it'
      }, callback);
      $(this).fadeOut();
      return false;
    });
    return $('a.read_later').live('click', function() {
      $.post($(this).attr('href') + '.json', {
        mark: 'read_later'
      }, callback);
      $(this).fadeOut();
      return false;
    });
  });
}).call(this);
(function() {
  jQuery(function() {
    $("div#browse .book a").live('click', function(e) {
      var previous, viewer;
      $("a.active").removeClass('active');
      $(this).addClass('active');
      previous = $("div#viewer");
      previous.fadeOut(function() {
        return previous.remove();
      });
      $("div#browse").css('right', '400px');
      viewer = $("<div id='viewer'></div>");
      viewer.css('top', e.pageY - 50);
      $(viewer).load(this + "/view", function() {
        $("body").append(viewer);
        return viewer.fadeIn();
      });
      return false;
    });
    $("a#show_all_books").click(function() {
      var self;
      $(".load-shelf").slideDown();
      self = $(this);
      if (self.data('loaded') == null) {
        self.data('loaded', true);
        $.getScript($(this).attr('href') + ".js");
      }
      return false;
    });
    $("a#hide_all_books").click(function() {
      return $(".load-shelf").slideUp();
    });
    $("#viewer a.back").live('click', function() {
      $("div#viewer").fadeOut(function() {
        $("a.active").removeClass('active');
        $(this).remove();
        return $("div#browse").css('right', '0px');
      });
      return false;
    });
    $(".download_url a, .description a").live('click', function() {
      window.open($(this).attr('href'), '_blank');
      return false;
    });
    return $("a.open-shelf").click(function() {
      var books, url;
      books = $(this).parents('.shelf').children('.load-shelf');
      if (books.data('loaded') != null) {
        books.slideToggle();
      } else {
        books.show();
        url = $(this).attr('href');
        books.load(url + ".js");
        books.data('loaded', true);
      }
      return false;
    });
  });
}).call(this);
(function() {
  var search;
  search = function(term) {
    var url;
    if (term.length < 3) {
      return $("#search-error").text('Pon tres letras al menos, no?');
    } else {
      url = "/buscar/" + term;
      return window.location = url;
    }
  };
  jQuery(function() {
    $("div.search input[type=text]").change(function() {
      return search($(this).val());
    });
    $("div.search a").click(function() {
      search($("div.search input[type=text]").val());
      return false;
    });
    return $("div.search-results").highlight($("#search-term").text());
  });
}).call(this);
